#ifdef GL_ES
precision mediump float;
#endif

uniform float vme;uniform sampler2D cmt;uniform float tcy;uniform vec3 ldn;uniform vec4 lat;uniform vec4 lde;uniform vec4 lsr;uniform vec3 cpsn;uniform bool dph;varying vec4 u;varying vec3 v;varying vec3 x; void main(){float a=tcy*0.01;gl_FragColor.rgb=u.rgb;gl_FragColor.a=1.0;if(!dph){if(vme==0.0){gl_FragColor=(texture2D(cmt,gl_PointCoord)*gl_FragColor)-vec4(0.1,0.1,0.1,0.0);}else if(vme==1.0){gl_FragColor=(texture2D(cmt,gl_PointCoord)*gl_FragColor)+vec4(0.2,0.2,0.2,0.0);}gl_FragColor.a*=a;}else{gl_FragColor.a*=(0.3*a);}if(gl_FragColor.r<=1e-6&&gl_FragColor.g<=1e-6&&gl_FragColor.b<=1e-6){gl_FragColor.rgb=vec3(0.1,0.1,0.1);}}
